@misc{unknown-b,
  title = {The 2019-nCoV, which belongs to the genus betacoronavirus, is a single-stranded positive-strand 33 RNA virus that appears to be distinct from, but is related to, other coronaviruses, such as severe acute 34 respiratory syndrome-related coronavirus (SARSr-CoV) and Middle East respiratory syndrome 35 coronavirus (MERSr-CoV)[4-8]. Current studies have shown that 2019-nCoV has about an 89% 36 homology with bat SARS-like-CoVZXC21 and 82% homology with human SARS-CoV[9]. The 37 disease is highly contagious, and may rapidly develop into severe pneumonia, acute respiratory 38 distress syndrome (ARDS), multiple organ dysfunction syndrome (MODS), and death, so the top 39 priority for clinicians is to identify and treat the severest patients in the early stage. The aim of this 40 study was to assess the potential high-risk factors of 2019-nCoV severe pneumonia and provide 41 evidence for the screening of severely afflicted patients},
  language = {}
}
@misc{unknown-c,
  title = {All of the patients in this study were hospitalized in a respiratory department, the Respiratory Intensive 45 Care Unit (RICU), from January 1, 2020 to February 10, 2020. The patients were all admitted to the 46 hospital because they were infected with 2019-nCoV and suffered from various kinds of symptoms, 47 including fever, dyspnea, cough, and fatigue. Every patient had completed the relevant laboratory 48 examination, including various common pathogen detection and chest computed tomographic (CT) 49 scans. All of the patients were local residents of Wuhan. Moreover, most of the patients had a history 50 of exposure to the Huanan Seafood Wholesale Market or had made contact with people who had been 51 confirmed (or suspected) to have contracted the illness. The 2019-nCoV nucleic acid detection of 52 some patients was positive, while for others, it was negative. These results could have been caused by 53 the immaturity of the methods used for 2019-nCoV nucleic acid detection, which led to false negative},
  language = {}
}
